// --- FUNCTIONS ---

// Render Courses based on filter
function renderCourses(filter = "all") {
  courseListEl.innerHTML = "";
  let filtered = courses;
  if (filter !== "all") {
    filtered = courses.filter((c) => c.category === filter);
  }
  filtered.forEach((course) => {
    const teacher = teachers.find((t) => t.id === course.teacherId);
    const courseCard = document.createElement("div");
    courseCard.className = "course-card";

    courseCard.innerHTML = `
      <img src="${course.image}" alt="${course.name}" class="course-image" />
      <h3 class="course-title">${course.name}</h3>
      <p class="course-description">${course.description}</p>
      <div class="course-badges">${course.badges
        .map((b) => `<span class="course-badge">${b}</span>`)
        .join("")}</div>
      <p class="course-price">₦${course.price.toLocaleString()}</p>
      <div class="course-teacher" title="Contact ${teacher.name} on WhatsApp">
        <img src="${teacher.photo}" alt="${teacher.name}" class="teacher-photo" />
        <a href="${teacher.whatsapp}" target="_blank" rel="noopener noreferrer">Contact Teacher</a>
      </div>
      <video width="100%" controls preload="metadata" style="margin-top: 0.5rem;">
        <source src="${course.previewVideo}" type="video/mp4" />
        Your browser does not support the video tag.
      </video>
    `;

    courseListEl.appendChild(courseCard);
  });
}

// Render Teachers
function renderTeachers() {
  teacherListEl.innerHTML = "";
  teachers.forEach((teacher) => {
    const teacherCard = document.createElement("div");
    teacherCard.className = "teacher-card";
    teacherCard.innerHTML = `
      <img src="${teacher.photo}" alt="${teacher.name}" title="Contact ${teacher.name} on WhatsApp" />
      <h4>${teacher.name}</h4>
      <p>${teacher.bio}</p>
      <a href="${teacher.whatsapp}" target="_blank" rel="noopener noreferrer">Chat on WhatsApp</a>
    `;
    teacherListEl.appendChild(teacherCard);
  });
}

// Render Testimonials slider
function renderTestimonials() {
  testimonialSliderEl.innerHTML = "";
  testimonials.forEach(({ quote, student }) => {
    const testimonialCard = document.createElement("div");
    testimonialCard.className = "testimonial-card";
    testimonialCard.innerHTML = `
      <p>"${quote}"</p>
      <span>- ${student}</span>
    `;
    testimonialSliderEl.appendChild(testimonialCard);
  });
}

// Update Profile Enrollment List
function updateProfile() {
  profileEnrollmentsEl.innerHTML = "";
  if (currentUser.enrolledCourses.length === 0) {
    profileEnrollmentsEl.innerHTML = "<li>You have not enrolled in any courses yet.</li>";
  } else {
    currentUser.enrolledCourses.forEach((courseId) => {
      const course = courses.find((c) => c.id === courseId);
      if (course) {
        const li = document.createElement("li");
        li.textContent = `${course.name} - ₦${course.price.toLocaleString()}`;
        profileEnrollmentsEl.appendChild(li);
      }
    });
  }
}

// Enrollment and payment simulation
function handlePaymentSubmission() {
  const file = paymentReceiptInput.files[0];
  if (!file) {
    alert("Please upload your payment receipt before submitting.");
    return;
  }
  // Simulate receipt validation & enrollment
  // For demo: just accept and enroll in first course displayed in filter
  const selectedCourse = courses[0]; // could enhance to select a specific course

  if (currentUser.enrolledCourses.includes(selectedCourse.id)) {
    alert("You are already enrolled in this course.");
  } else {
    currentUser.enrolledCourses.push(selectedCourse.id);
    // Add user to registeredStudents for admin dashboard (simple simulation)
    if (!registeredStudents.find((s) => s.name === currentUser.name)) {
      registeredStudents.push({
        name: currentUser.name,
        enrolledCourses: [...currentUser.enrolledCourses],
      });
    } else {
      const student = registeredStudents.find((s) => s.name === currentUser.name);
      student.enrolledCourses = [...currentUser.enrolledCourses];
    }
    alert(`Payment receipt accepted! You are now enrolled in "${selectedCourse.name}".`);
    updateProfile();
    paymentReceiptInput.value = "";
  }
}

// Filter button click handler
courseFilterBtns.forEach((btn) => {
  btn.addEventListener("click", () => {
    courseFilterBtns.forEach((b) => b.classList.remove("active"));
    btn.classList.add("active");
    renderCourses(btn.dataset.filter);
  });
});

// --- LIVE CHAT FUNCTIONS ---
function addChatMessage(msg, sender = "admin") {
  const msgEl = document.createElement("div");
  msgEl.className = sender === "user" ? "user-msg" : "admin-msg";
  msgEl.textContent = msg;
  chatMessagesEl.appendChild(msgEl);
  chatMessagesEl.scrollTop = chatMessagesEl.scrollHeight;
}

// Initialize chat with welcome message
function initChat() {
  chatMessagesEl.innerHTML = "";
  addChatMessage("Hello! How can we assist you today?");
}

liveChatBtn.addEventListener("click", () => {
  liveChatWindow.classList.remove("hidden");
  liveChatBtn.classList.add("hidden");
  initChat();
  chatInput.focus();
});

chatCloseBtn.addEventListener("click", () => {
  liveChatWindow.classList.add("hidden");
  liveChatBtn.classList.remove("hidden");
});

chatSendBtn.addEventListener("click", () => {
  const msg = chatInput.value.trim();
  if (!msg) return;
  addChatMessage(msg, "user");
  chatInput.value = "";

  // Simulated bot response delay
  setTimeout(() => {
    addChatMessage("Thank you for your message. Our team will get back to you shortly.");
  }, 1000);
});

chatInput.addEventListener("keypress", (e) => {
  if (e.key === "Enter") {
    chatSendBtn.click();
  }
});

// --- ADMIN DASHBOARD ---

function renderAdminDashboard() {
  adminStudentsList.innerHTML = "";
  registeredStudents.forEach((student) => {
    const div = document.createElement("div");
    div.innerHTML = `
      <strong>${student.name}</strong>
      <ul>
        ${student.enrolledCourses
          .map((cid) => {
            const course = courses.find((c) => c.id === cid);
            return course ? `<li>${course.name}</li>` : "";
          })
          .join("")}
      </ul>
    `;
    div.style.backgroundColor = "#e0ffe0";
    div.style.marginBottom = "0.5rem";
    div.style.padding = "0.5rem";
    div.style.borderLeft = "5px solid #4caf50";
    adminStudentsList.appendChild(div);
  });
}

openAdminBtn.addEventListener("click", () => {
  adminDashboard.classList.remove("hidden");
  openAdminBtn.style.display = "none";
  renderAdminDashboard();
});

if (closeAdminBtn) {
  closeAdminBtn.addEventListener("click", () => {
    adminDashboard.classList.add("hidden");
    openAdminBtn.style.display = "block";
  });
}

// Payment submission
submitPaymentBtn.addEventListener("click", handlePaymentSubmission);

// Initial renders
renderCourses();
renderTeachers();
renderTestimonials();
updateProfile();

